        this.deletePost = this.deletePost.bind(this);

        this.state = {post: []}
    }

    componentDidMount(){
        axios.get('http://localhost:5000/posts/')
            .then(response =>{
                    this.setState({posts: response.data})
            })
            .catch((error) => {
                console.log(error);
            })
    }

    deletePost(id){
        axios.delete('http://localhost:5000/posts/'+id)
